import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navigation = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'QuillBot Flow', icon: '‚úçÔ∏è' },
    { path: '/enhanced-paraphraser', label: 'Literary Paraphraser', icon: 'üé®' },
    { path: '/grammar-checker', label: 'Grammar Pro', icon: 'üìù' },
    { path: '/character-assistant', label: 'Character Dev', icon: 'üë•' },
    { path: '/plot-analyzer', label: 'Plot Structure', icon: 'üìä' },
    { path: '/manuscript-manager', label: 'Manuscript Manager', icon: 'üìñ' },
    { path: '/scene-builder', label: 'Scene Builder', icon: 'üé™' },
    { path: '/readability-optimizer', label: 'Readability', icon: 'üìä' },
    { path: '/summarizer', label: 'Summarizer', icon: 'üìÑ' },
    { path: '/tone-analyzer', label: 'Tone Analyzer', icon: 'üé≠' }
  ];

  return (
    <nav className="navigation">
      <div className="nav-brand">
        <h1>Stefano De Alman</h1>
      </div>
      <ul className="nav-links">
        {navItems.map((item) => (
          <li key={item.path}>
            <Link 
              to={item.path} 
              className={location.pathname === item.path ? 'active' : ''}
            >
              <span className="nav-icon">{item.icon}</span>
              {item.label}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default Navigation;
